Buat file app.py dan tulis kode Flask berikut: 
from flask import Flask, jsonify, request
app = Flask(__name__)
# Data dummy
users = [{"id":1,"name":"Eko","email":"eko@cukprodev.com"},{"id":2,"name":"Adam","email":"adam@cukprodev.com"}]
@app.route('/users', methods=['GET'])
def get_users():
  return jsonify(users)
@app.route('/users', methods=['POST'])
def add_user():
  data = request.get_json()
  users.append(data)
  return jsonify({"message":"User ditambahkan","data":data}), 201
@app.route('/users/<int:id>', methods=['PUT'])
def update_user(id):
  for user in users:
    if user['id']==id:
      user.update(request.get_json())
      return jsonify({"message":"User diperbarui","data":user})
  return jsonify({"error":"User tidak ditemukan"}),404
@app.route('/users/<int:id>', methods=['DELETE'])
def delete_user(id):
  global users
  users = [u for u in users if u['id']!=id]
  return jsonify({"message":"User dihapus"})
if __name__ == "__main__":
  app.run(debug=True)

